def largest_product_in_series(series, n):
    max_product = 0
    length = len(series)
    
    for i in range(length - n + 1):
        product = 1
        for j in range(n):
            product *= int(series[i + j])
        if product > max_product:
            max_product = product
            
    return max_product  

